SELECT 
FROM `article`
WHERE `username` = #{username, jdbcType=VARCHAR}
AND `status` = #{status, jdbcType=TINYINT}

    AND `create_time`  <  #{lastDateTime, jdbcType=BIGINT}


    AND `create_time`  >=  #{lessDateTime, jdbcType=BIGINT}

ORDER BY `create_time` DESC limit 5;

